package com.dcoletto.sharedprefdao.editorenum class SharedPrefType (private val humanName: String) {    STRING("String"),    INTEGER("Integer"),    BOOLEAN("Boolean"),    FLOAT("Float"),    LONG("Long"),    STRING_SET("String Set (separator: `,`)");    override fun toString(): String {        return humanName    }    companion object {        fun fromPrefValue(prefValue: Any): SharedPrefType {            return when (prefValue) {                is Boolean -> BOOLEAN                is Float -> FLOAT                is Int -> INTEGER                is Long -> LONG                is String -> STRING                is Set<*> -> STRING_SET                else -> throw IllegalArgumentException("Unrecognized type for value [$prefValue]")            }        }    }}